<div id="lightbox" class="modal fade" tabindex="-1" role="dialog" aria-labelledby="myLargeModalLabel" data-target=".bs-example-modal-lg" aria-hidden="true">
  <div class="modal-dialog modal-lg "  >
    <button type="button" class="close" data-dismiss="modal" aria-label="Close"><i class="fa fa-times-circle"></i></button>
    <div class="modal-content ">
      <div class="modal-body"> <img onerror='imgError(this);' src="" alt="" /> </div>
    </div>
  </div>
